import React, { useEffect, useState } from 'react'
import './Header.scss'
import ScrollTop from '../ScrollTop'
import { Link, useLocation } from 'react-router-dom';
export default function Header() {
  const [isScrolledDown, setIsScrolledDown] = useState(false);
  const [homePage, sethomePage] = useState(false)
  const location = useLocation()
  useEffect(() => {
    if(location.pathname=='/'){
      sethomePage(true)
    }
    const handleScroll = () => {
      const scrolled = window.scrollY > 0;
      setIsScrolledDown(scrolled);
    };
    window.addEventListener('scroll', handleScroll);
    // Clean up the event listener when the component unmounts
    return () => {
      window.removeEventListener('scroll', handleScroll);
    };
  }, []);


  return (
    <>
      <div className='main__header'>
        <header id="header" className={`header d-flex align-items-center fixed-top ${isScrolledDown ? 'sticked' : ''}`}>
          <div className="container-fluid container-xl d-flex align-items-center justify-content-between">

            <Link to='/' className={`logo d-flex align-items-center ${homePage ? 'logo-home' : '' }`}>
              <img src="assets/img/logo.svg" alt="" />
            </Link>

            <i className="mobile-nav-toggle mobile-nav-show bi bi-list"></i>
            <i className="mobile-nav-toggle mobile-nav-hide d-none bi bi-x"></i>
            <nav className="navbar">
              <div className="btn btn-primary">Contact</div>
              <div className={`header d-flex align-items-center fixed-top ${isScrolledDown ? 'sticked' : ''}`}>
                <div class="one"></div>
                <div class="two"></div>
                <div class="three"></div>
              </div>
            </nav>

          </div>
        </header>

      </div>
    </>
  )
}
